import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Header from "./header";
import Footer from "./footer";
import './Login.css';

const LoginPage = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        console.log("i here in handleLogin")
        e.preventDefault();
        try {
            console.log('Sending login request:', { username, password });

            const response = await axios.post('http://localhost:3000/logIn', {
                user_name: username,
                password: password
            }, {
                headers: {
                    'Content-Type': 'application/json'
                }
            });
            console.log(response.data)
            if (response.data.user ) {
                const userWithoutPassword = {
                    user_id: response.data.user.user_id,
                    user_name: response.data.user.user_name,
                    user_type: response.data.user.user_type
                };
                localStorage.setItem(response.data.user.user_name, JSON.stringify(userWithoutPassword)); // Save customerId to localStorage
                if (response.data.user.user_type==='employee'){
                    navigate(`/EmployeeArea?id=${user.user_id}`);
                }
                else if(response.data.user.user_type==='manager'){
                    navigate(`/ManagerArea?id=${user.user_id}`)
                }
                else{
                    navigate(`/CustomerArea?id=${user.user_id}`);                    
                }
            
            } else {
                setError('Invalid username or password, please try again');
            }
        } catch (error) {
            console.error('Error during login:', error);
            setError('Server error');
        }
    };

    return (
        <div className="main-container">
            <Header />
            <div className="body">
                <div className="grey">
                    <hr className="Login" />
                    <h589>Login to the personal area:</h589>
                    <form onSubmit={handleLogin}>
                        <label>Username:
                            <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
                        </label>
                        <br />
                        <label>Password:
                            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                        </label>
                        <br />
                        <button className="btn_enter" type="submit">Login</button>
                    </form>
                    {error && <div className="error-message">{error}</div>}
                </div>
            </div>
        </div>
    );
};

export default LoginPage;
