import React, { useState } from 'react';
import './CustomerPersonalDetails.css';
import CustomerSideBar from './CustomerSideBar';
import Header from "./header";

const CustomerPersonalDetails = () => {
    const [customer, setCustomer] = useState({
        firstName: 'Yahlom',
        lastName: 'Levi',
        phoneNumber: '052-567-9615',
        email: 'yahloml@walla.com',
        address: '123 Main Street, Ashdod',
        idNumber: '206617417',
        customerImage: 'default-customer-image.jpg',
        isEditable: false
    });
    const [isImageUploaderOpen, setImageUploaderOpen] = useState(false);
    const [message, setMessage] = useState('');

    const handleImageChange = (event) => {
        const file = event.target.files[0];
        const reader = new FileReader();
        reader.onload = (e) => {
            setCustomer(prevState => ({ ...prevState, customerImage: e.target.result }));
        };
        reader.readAsDataURL(file);
    };

    const toggleEdit = () => {
        setCustomer(prev => ({ ...prev, isEditable: !prev.isEditable }));
        setMessage('');
    };

    const saveDetails = () => {
        setMessage('Details have been updated successfully!');
    };

    return (
        <div className="CustomerPersonalDetails">
            <Header/>
            <CustomerSideBar />
            <div className="container2">
                <h45>Personal Information</h45>
                <div id="customerDetails">
                    <img id="customerImage" src={customer.customerImage} alt="Customer" />

                    <div className="button-container">
                        <button onClick={() => setImageUploaderOpen(!isImageUploaderOpen)}>Upload Image</button>
                        {isImageUploaderOpen && (
                            <input type="file" id="imageUploadInput" accept="image/*" onChange={handleImageChange} />
                        )}
                        <button onClick={toggleEdit}>{customer.isEditable ? 'Cancel Edit' : 'Update Details'}</button>
                        {customer.isEditable && <button onClick={saveDetails}>Save Changes</button>}
                    </div>
                    {message && <p>{message}</p>}

                    <label htmlFor="firstName">First Name:</label>
                    <input type="text" id="firstName" value={customer.firstName} disabled={!customer.isEditable} />

                    <label htmlFor="lastName">Last Name:</label>
                    <input type="text" id="lastName" value={customer.lastName} disabled={!customer.isEditable} />

                    <label htmlFor="idNumber">ID Number:</label>
                    <input type="text" id="idNumber" value={customer.idNumber} disabled />

                    <label htmlFor="phoneNumber">Phone Number:</label>
                    <input type="text" id="phoneNumber" value={customer.phoneNumber} disabled={!customer.isEditable} />

                    <label htmlFor="email">Email Address:</label>
                    <input type="email" id="email" value={customer.email} disabled={!customer.isEditable} />

                    <label for="address">Address:</label>
                    <textarea id="address" rows="4" disabled={!customer.isEditable} defaultValue={customer.address}></textarea>
                </div>
            </div>
        </div>
    );
};

export default CustomerPersonalDetails;
